// Scroll Up Button

const Fnavbar = document.querySelector(".sticky-header");
window.addEventListener("scroll", function () {
    if (window.pageYOffset > 400 && window.pageYOffset < 8400) {
        document.querySelector(".sticky-header").style.top = '0px';
        document.querySelector(".sticky-header").style.opacity = 1;
        document.querySelector(".sticky-header").style.visibility = 'vissible';
    }
    else {
        document.querySelector(".sticky-header").style.top = '-100px';
    }
})

